import React from 'react'
import { useState } from 'react';
import './uploadimage.css'
import {uploadImages} from '../services/postApi'
export default function Uploadimage1(props) {

 const {uploadImage,setUploadImage} =props  
 
  const fileTypes = ['image/png', 'image/jpeg'];
  const handleimgPreview =   (e) => {
        let filesArr = (e.target.files.filelist);
        console.log(filesArr);
        const images= filesArr.reduce((acc, v)=>  
             (v.name && fileTypes.includes(v.type)) ? [...acc,URL.createObjectURL(v) ] : acc,[])
        setUploadImage(images)          
}



  return (
 <>
 <div class="image-upload">
 <label for="file-input">
<img style={{position:'fixed',right:'25px',bottom:'25px' ,height:'40px',width:'40px'
}} 
src="https://img.icons8.com/office/80/000000/upload--v1.png"/>
 </label>
 <input multiple draggable={true} id="file-input" type="file" onChange={handleimgPreview} />
</div>
{uploadImage.map((i,id)=>  <img  src={i} alt='k'></img>  
)}
 </>
  )
}
