import React, { useState } from "react";
import ImageUploading from "react-images-uploading";
import './buttongradients/buttongradients.css'
import { Delete ,Update } from "../icons/icons";

function UploadAndDisplayImage() {

   
  const [images, setImages] = useState([]);
  const maxNumber = 5;
  const onChange = (imageList, addUpdateIndex) => {
    // data for submit
    console.log(imageList, addUpdateIndex);
    setImages(imageList);
  };

  return (<>
    <div style={{ width :"100%",display:'flex',height:'480px',overflowY:'auto',position:'relative',backgroundColor:'lightblue'}} >
       
      <ImageUploading
        multiple
        value={images}
        onChange={onChange}
        maxNumber={maxNumber}
        dataURLKey="data_url"
      >
        {({
          imageList,
          onImageUpload,
          onImageRemoveAll,
          onImageUpdate,
          onImageRemove,
          isDragging,
          dragProps
        }) => (
          // write your building UI
          <div className="upload__image-wrapper" style={{width:'100%'}}  >
            <div style={{display:"flex",height:'30px',justifyContent:'center',gap:'10px',width:'100%',marginTop:'5px'}}>
              <button
              style={isDragging ? { color: "red" } : {width:'auto',height:'30px',fontSize:'10px',bc}}
            
              onClick={onImageUpload}
              {...dragProps}
            >
              drag or select
              
            </button>
            &nbsp;
            <button onClick={onImageRemoveAll} 
           
            >Remove all </button></div>
            
            {imageList.map((image, index) => (
              <div key={index} className="image-item" style={{width:'200px'}}>
                <img src={image.data_url} alt=""  style={{width:'100px',heigth:'100px'}}/>
                <div className="image-item__btn-wrapper" style={{display:'flex',justifyContent:'space-evenly'}}>
                  <button onClick={() => onImageUpdate(index)}>{Update}</button>
                  <button onClick={() => onImageRemove(index)}>{Delete}</button>
                </div>
              </div>
            ))}
          </div>
        )}
      </ImageUploading>
           
    </div>
    </>
  );
}



export default UploadAndDisplayImage;
