import { CopyToClipboard } from "react-copy-to-clipboard";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
const img = "https://images.pexels.com/photos/302743/pexels-photo-302743.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940"
export default function Copyurl() {
 return (
   <div className="App">
   <ToastContainer
position="top-center"
autoClose={2000}
hideProgressBar={false}
newestOnTop={false}
closeOnClick
rtl={false}
pauseOnFocusLoss
draggable

/>
     <h1>Clipboard Copy</h1>
     <CopyToClipboard
     text={img}
     onCopy={() => toast('copied')}>
      <svg xmlns="http://www.w3.org/2000/svg" width="24"  f height="24" viewBox="0 0 24 24">
          <path d="M22 6v16h-16v-16h16zm2-2h-20v20h20v-20zm-24 17v-21h21v2h-19v19h-2z"/></svg>
     </CopyToClipboard>
   </div>
 );
}
