import React, { useState } from "react";
// import ReactDOM from "react-dom";
import ImageUploading from "react-images-uploading";

// import "./styles.css";
const kloudrac='https://scontent.fdel5-2.fna.fbcdn.net/v/t1.6435-9/86651946_2618139595075794_2134622011949842432_n.jpg?_nc_cat=107&ccb=1-5&_nc_sid=09cbfe&_nc_ohc=XnVw1VQRFegAX-MHw0Z&_nc_ht=scontent.fdel5-2.fna&oh=00_AT8FhrSOTeYGz4_SOgVXgotE_vt79tlP5zLfSNgndwPwcg&oe=6294793C'  
function UploadAndDisplayImage() {

    const [leadersSpeak1, setleadersSpeak1] = useState('')
  const [images, setImages] = useState([]);
  const maxNumber = 5;
  const onChange = (imageList, addUpdateIndex) => {
    // data for submit
    console.log(imageList, addUpdateIndex);
    setImages(imageList);
  };
console.log(leadersSpeak1);
  return (<>
    <div style={{ width :"100%",display:'flex'}} >
       
      <ImageUploading
        multiple
        value={images}
        onChange={onChange}
        maxNumber={maxNumber}
        dataURLKey="data_url"
      >
        {({
          imageList,
          onImageUpload,
          onImageRemoveAll,
          onImageUpdate,
          onImageRemove,
          isDragging,
          dragProps
        }) => (
          // write your building UI
          <div className="upload__image-wrapper">
            <button
              style={isDragging ? { color: "red" } : null}
              onClick={onImageUpload}
              {...dragProps}
            >
              Click or Drop here
            </button>
            &nbsp;
            <button onClick={onImageRemoveAll}>Remove all images</button>
            {imageList.map((image, index) => (
              <div key={index} className="image-item">
                <img src={image.data_url} alt="" width="100" />
                <div className="image-item__btn-wrapper">
                  <button onClick={() => onImageUpdate(index)}>Update</button>
                  <button onClick={() => onImageRemove(index)}>Remove</button>
                </div>
              </div>
            ))}
          </div>
        )}
      </ImageUploading>
<textarea name="" id=""onChange={(e)=>{setleadersSpeak1(e.target.value)}}  value={leadersSpeak1} cols="100" rows="10"></textarea>
           
    </div>

    <pre style={{font:"caption    "}}>    {leadersSpeak1}</pre>
    </>
  );
}



export default UploadAndDisplayImage;
