import axios from "axios"
import { URL } from "./endpoint";
export const postLetter = async (letter) => {

    const formData = new FormData();

    const template_code = JSON.stringify(letter)
   
  formData.append(template_code
    )
    return await axios.post(`${URL}/addTemplate`,{ template_code}),{
        headers:{
            length:'50mb',
            'Content-Type': 'application/x-www-form-urlencoded',
            'Content-Length': template_code.length
        }
    }

      

}
