import React, { useState } from 'react'
import './chipsCss/emailchips.css'
export default function Chips(props) {
  const {} = props
    const [value, setValue] = useState({
        val: "",
        chips: []
    })    
    const handleChange = (e) => {
        setValue((prev)=>({...prev, val: e.target.value }))
        console.log(value);
    }
    const handleKeyDown = (e) => {
        if (['Enter', 'Tab', ','].includes(e.key)) {
            var chip = value.val;
            setValue((pre)=>({
                ...pre,
                val: "",
                chips: [...pre.chips, chip]
            }))
        }
    }
    const handleDelete = (toBeRemoved) => {
        setValue({
            chips: value.chips.filter(email => email !== toBeRemoved)
        });
    };
    return (
        <>
            <div style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center"
            }}>
                <div className='wrapper'>
                    {value.chips?.map((email, id) => <div key={id}
                        style={{ display: "flex ", backgroundColor: "lightgray", padding: "5px", gap: "3px", borderRadius: "30px" }}>
                        {email}
                        <button style={{ border: "none", backgroundColor: 'white', borderRadius: "30px" }}
                            type="button"
                            onClick={() => handleDelete(email)}>
                            &times;
                        </button>
                    </div>)}
                    <input type='text' style={{ border: "none", width: "100%" }}
                        value={value.val}
                        onChange={(e) => handleChange(e)}
                        onKeyDown={(e) => handleKeyDown(e)}
                    />
                </div>
            </div>
        </>
    )
}
