import React, { useRef, useState } from 'react';
import { Editor } from '@tinymce/tinymce-react';
import newsletter from '../newsletter'
import '../App.css'
import { postLetter } from '../services/postApi';
export default function EditorPage() {
  const editorRef = useRef(null);
  const [value, setValue] = useState(`${newsletter}`)

  const log = () => {
    if (editorRef.current) {
      setValue(editorRef.current.getContent())

    }
  };

  const sendLetter = async (value) => {

    await postLetter(value).then(res => { console.log(res) }).catch(err => { console.log(err) })
  }

  return (
    <>
      <div style={{ display: 'flex' ,gap:'10px',margin:'5PX'}}>
      <div style={{width:'30'}}>
      <button className='btn' style={{ backgroundColor: 'lightblue', border: 'none', height: '50px', width: '100px', margin: '10px' }} onClick={log}>Log content HTML</button>
        <button onClick={() => { sendLetter(value) }}>Send</button>

</div>
      

        <Editor
          apiKey='cm0s5oticy0ahny0l3o0pdpyionk5z8jvftdcnjmp4p0py4y'
          onInit={(evt, editor) => editorRef.current = editor}
          initialValue={value}
          onChange={(e) => setValue(e.target.value)}
          // init={{
          //   height: 1000,
          //   // menubar: false,
          //   selector:'.textEditor',
          //   font_formats: "Andale Mono=andale mono,times; Arial=arial,helvetica,sans-serif; Arial Black=arial black,avant garde; Book Antiqua=book antiqua,palatino; Comic Sans MS=comic sans ms,sans-serif; Courier New=courier new,courier; Georgia=georgia,palatino; Helvetica=helvetica; Impact=impact,chicago; Oswald=oswald; Symbol=symbol; Tahoma=tahoma,arial,helvetica,sans-serif; Terminal=terminal,monaco; Times New Roman=times new roman,times; Trebuchet MS=trebuchet ms,geneva; Verdana=verdana,geneva; Webdings=webdings; Wingdings=wingdings,zapf dingbats",
          //   plugins: [
          //     'advlist', 'autolink', 'lists', 'link', 'image', 'charmap',
          //     'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
          //     'insertdatetime', 'media', 'table', 'preview', 'help', 'wordcount'
          //   ],
          // toolbar:'table'+'| undo redo | blocks | fontselect ' +
          //   'bold italic forecolor | alignleft aligncenter ' +
          //   'alignright alignjustify | bullist numlist outdent indent | ' +
          //   'removeformat | help' + 
          //   'link image',
          //   content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }',
          //   paste_data_images: true,
          //   plugins: 'image code',
          //   height: 400
          // }}
          init={{
            height: 0,
            selector: '.texteditor',
            plugins: "advlist lists table paste textcolor fullscreen colorpicker tabfocus link preview autolink image charmap image code",
            toolbar: `undo redo table fontsizeselect bold italic underline forecolor backcolor bullist numlist link blocks charmap fontselect alignleft aligncenter alignright alignjustify outdent indent removeformat image media code insertdatetime preview link image`,
            imagetools_toolbar: "rotateleft rotateright | flipv fliph | editimage",
            paste_data_images: true,
            menubar: true,
            toolbar_items_size: 'large',
            min_height: 500,
            max_height: 1000,
            content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }',
            convert_newlines_to_brs: false,
            statusbar: false,
            relative_urls: false,
            remove_script_host: false,
            language: 'en',


          }}
        />

        
      </div>
    </>
  );
}
