import React, { useState } from "react";
import ImageUploading from "react-images-uploading";
import './buttongradients/buttongradients.css'
import { Delete, Update } from "../icons/icons";

function UploadAndDisplayImage() {


  const [images, setImages] = useState([]);
  const maxNumber = 69;
  const onChange = (imageList, addUpdateIndex) => {
    // data for submit
    console.log(imageList, addUpdateIndex);
    setImages(imageList);
  };

  return (<>
    <div style={{ width: "100%", display: 'flex', height: '500px',borderRadius:'10px', overflowY: 'auto', position: 'relative', backgroundColor: '#E5E5E5' }} >

      <ImageUploading
        multiple
        value={images}
        onChange={onChange}
        maxNumber={maxNumber}
        dataURLKey="data_url"
      >
        {({
          imageList,
          onImageUpload,
          onImageRemoveAll,
          onImageUpdate,
          onImageRemove,
          isDragging,
          dragProps
        }) => (
          <div className="upload__image-wrapper" style={{ width: '100%' }}  >
            <div style={{ display: "flex", height: '30px', justifyContent: 'center',gap: '10px', width: '100%', marginTop: '10px' }}>
              <button
                style={isDragging ? { color: "red", width: 'auto', height: '30px', fontSize: '10px' ,overflow:'hidden'} : {
                  width: 'auto', height: '30px', fontSize: '10px',
                  border: 'none', borderRadius: '10px', color: 'white',overflow:'hidden',
                  backgroundImage: "linear-gradient(to right, #CDB599FF 0%, #CDB599FF 51%, #CDB599FF 100%)"

                }}

                onClick={onImageUpload}
                {...dragProps}
              >
                drag or select

              </button>
              &nbsp;
              <button onClick={onImageRemoveAll}
                style={{
                  width: 'auto', height: '30px', fontSize: '10px',overflow:'hidden',
                  border: 'none', borderRadius: '10px', color: 'white',
                  backgroundImage:  "linear-gradient(to right, #CDB599FF 0%, #CDB599FF 51%, #CDB599FF 100%)"
                }}

              >Remove all </button></div>

            
              <div style={{height:'auto',width:'100%',display:'flex',flexWrap:'wrap',justifyContent:'space-evenly',gap:'5px'}}>
              {imageList.map((image, index) => (
              <div key={index} className="image-item"  style={{display:'flex',flexDirection:'column',height:'200px',width:'100px',margin:'3px'}} >
                <img src={image.data_url} alt=""  style={{height:'80%',width:'100%',objectFit:'contain'}}/>
                <div className="image-item__btn-wrapper" style={{ height:'20%' ,display: 'flex', justifyContent: 'space-evenly' }}>
                  <button onClick={() => onImageUpdate(index)}
                    style={{ backgroundColor: 'transparent', border: 'none' }}
                  >{Update}</button>
                  <button onClick={() => onImageRemove(index)}
                    style={{ backgroundColor: 'transparent', border: 'none' }}
                  >{Delete}</button>
                </div>
              </div>
              ))}
              </div>
           

          </div>
        )}
      </ImageUploading>

    </div>
  </>
  );
}



export default UploadAndDisplayImage;
