import { CopyToClipboard } from "react-copy-to-clipboard";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
const img = "https://images.pexels.com/photos/302743/pexels-photo-302743.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940"
export default function Copyurl() {
 return (
   <div className="App">
   <ToastContainer
position="top-center"
autoClose={2000}
hideProgressBar={false}
newestOnTop={false}
closeOnClick
rtl={false}
pauseOnFocusLoss
draggable
pauseOnHover
/>
     <h1>Clipboard Copy</h1>
     <CopyToClipboard
     text={img}
     onCopy={() => toast('copied')}>
       <span>Copy to clipboard with span</span>
     </CopyToClipboard>
   </div>
 );
}
