import axios from "axios"
import { URL } from "./endpoint";
export const postLetter = async  (letter) =>{

  const template_code = JSON.stringify({letter})
  const formData =new FormData();
  formData.append("template_code",template_code)
  return axios.post(`${URL}/addTemplate`,formData);

}
