  const { setUploadImage, uploadImage } = props


  const fileTypes = ['image/png', 'image/jpeg'];
  const handleimgPreview = (e) => {
    let selected = e.target.files[0];
    if (selected.name && fileTypes.includes(selected.type)) {
      console.log(selected.type)
      setUploadImage([...uploadImage, URL.createObjectURL(selected)])
      console.log(uploadImages);
    }
  }
  return (
    <>
      <div class="image-upload">
        <label for="file-input">
          <img style={{ position: 'fixed', right: '10px', bottom: '10px' }} src="https://img.icons8.com/office/80/000000/upload--v1.png" />
        </label>
        <input multiple draggable={true} id="file-input" type="file" onChange={handleimgPreview} />
      </div>
      {/* {uploadImage.map((i,id)=>  <img  src={i} alt='k'></img>  
)} */}
    </>
  )
}
