import React, { useState } from "react";
import ImageUploading from "react-images-uploading";
import './buttongradients/buttongradients.css'
import { Delete, Update } from "../icons/icons";
import './uploadimage.css'
function UploadAndDisplayImage() {


  const [images, setImages] = useState([]);
  const maxNumber = 69;
  const onChange = (imageList, addUpdateIndex) => {
    // data for submit
    console.log(imageList, addUpdateIndex);
    setImages(imageList);
  };

  images.map((img)=>{console.log(img);})

  return (<>
    <div >

      <ImageUploading
        multiple
        value={images}
        onChange={onChange}
        maxNumber={maxNumber}
        dataURLKey="name"
      >
        {({
          imageList,
          onImageUpload,
          onImageRemoveAll,
          onImageUpdate,
          onImageRemove,
          isDragging,
          dragProps
        }) => (
          <div className="upload__image-wrapper" style={{ width: '100%' }}  >
            <div style={{ display: "flex", height: '30px', justifyContent: 'center', gap: '10px', width: '100%', marginTop: '10px' }}>
              <button
                style={isDragging ? { color: "red", width: 'auto', height: '30px', fontSize: '10px', overflow: 'hidden' } : {
                  width: 'auto', height: '30px', fontSize: '10px',
                  border: 'none', borderRadius: '10px', color: 'white', overflow: 'hidden',
                  backgroundImage: "linear-gradient(to right, #CDB599FF 0%, #CDB599FF 51%, #CDB599FF 100%)"

                }}

                onClick={onImageUpload}
                {...dragProps}
              >
                drag or select

              </button>
              &nbsp;
              <button onClick={onImageRemoveAll}
                style={{
                  width: 'auto', height: '30px', fontSize: '10px', overflow: 'hidden',
                  border: 'none', borderRadius: '10px', color: 'white',
                  backgroundImage: "linear-gradient(to right, #CDB599FF 0%, #CDB599FF 51%, #CDB599FF 100%)"
                }}

              >Remove all </button></div>

<div class="image-upload">
  <label for="file-input">
  <svg style={{borderRadius:'50%',
  padding:'10px',boxShadow:' rgba(0, 0, 0, 0.25) 0px 54px 55px, rgba(0, 0, 0, 0.12) 0px -12px 30px, rgba(0, 0, 0, 0.12) 0px 4px 6px, rgba(0, 0, 0, 0.17) 0px 12px 13px, rgba(0, 0, 0, 0.09) 0px -3px 5px'}}
   xmlns="http://www.w3.org/2000/svg" width="40" height="40" fill="red" class="bi bi-upload" viewBox="0 0 16 16">
  <path d="M.5 9.9a.5.5 0 0 1 .5.5v2.5a1 1 0 0 0 1 1h12a1 1 0 0 0 1-1v-2.5a.5.5 0 0 1 1 0v2.5a2 2 0 0 1-2 2H2a2 2 0 0 1-2-2v-2.5a.5.5 0 0 1 .5-.5z"/>
  <path d="M7.646 1.146a.5.5 0 0 1 .708 0l3 3a.5.5 0 0 1-.708.708L8.5 2.707V11.5a.5.5 0 0 1-1 0V2.707L5.354 4.854a.5.5 0 1 1-.708-.708l3-3z"/>
</svg>
  </label>

  <input multiple draggable={true} id="file-input" type="file" />
</div>
