import React, { useState } from "react";
import ImageUploading from "react-images-uploading";
import { Delete ,Update } from "../icons/icons";

function UploadAndDisplayImage() {

    const [leadersSpeak1, setleadersSpeak1] = useState('')
  const [images, setImages] = useState([]);
  const maxNumber = 5;
  const onChange = (imageList, addUpdateIndex) => {
    // data for submit
    console.log(imageList, addUpdateIndex);
    setImages(imageList);
  };
console.log(leadersSpeak1);
  return (<>
    <div style={{ width :"100%",display:'flex',margin:'10px',height:'480px',overflowY:'auto',position:'relative'}} >
       
      <ImageUploading
        multiple
        value={images}
        onChange={onChange}
        maxNumber={maxNumber}
        dataURLKey="data_url"
      >
        {({
          imageList,
          onImageUpload,
          onImageRemoveAll,
          onImageUpdate,
          onImageRemove,
          isDragging,
          dragProps
        }) => (
          // write your building UI
          <div className="upload__image-wrapper">
            <button
              style={isDragging ? { color: "red" } : null}
              onClick={onImageUpload}
              {...dragProps}
            >
              drag or select
            </button>
            &nbsp;
            <button onClick={onImageRemoveAll} 
            >Remove all </button>
            {imageList.map((image, index) => (
              <div key={index} className="image-item">
                <img src={image.data_url} alt=""  style={{width:'100px',heigth:'100px'}}/>
                <div className="image-item__btn-wrapper">
                  <button onClick={() => onImageUpdate(index)}>{Update}</button>
                  <button onClick={() => onImageRemove(index)}>{Delete}</button>
                </div>
              </div>
            ))}
          </div>
        )}
      </ImageUploading>
           
    </div>
    </>
  );
}



export default UploadAndDisplayImage;
